# 计算加权分数的函数
def calculate_weighted_scores(stats, weights):
    weighted_scores = []
    for i in range(len(stats['veh_waiting_time'])):
        weighted_score = {}
        for metric, weight in weights.items():
            weighted_score[metric] = stats[metric][i] * weight
        weighted_score['total'] = sum(weighted_score.values())
        weighted_scores.append(weighted_score)
    return weighted_scores

