# 计算各个模型的加权分数
weighted_scores = {}
for model, model_stats in stats.items():
    weighted_scores[model] = calculate_weighted_scores(model_stats, weights)

# 输出各个模型的加权分数
for model, scores in weighted_scores.items():
    print(f"Weighted scores for {model}:")
    for i, score in enumerate(scores):
        print(f"Episode {i + 1}: {score}")


# 比较各个模型在不同指标上的表现
def compare_models_on_metrics(weighted_scores):
    metrics = list(weights.keys())

    # 初始化存储每个模型在每个指标上的平均表现的字典
    avg_metric_scores = {model: {metric: 0 for metric in metrics} for model in weighted_scores}
    total_scores = {model: 0 for model in weighted_scores}

    # 计算每个模型在每个指标上的平均加权分数
    for model, scores in weighted_scores.items():
        for score in scores:
            for metric in metrics:
                avg_metric_scores[model][metric] += score[metric]
            total_scores[model] += score['total']
        for metric in metrics:
            avg_metric_scores[model][metric] /= len(scores)
        total_scores[model] /= len(scores)

    # 打印比较结果
    print("\nAverage Weighted Scores for Each Metric:")
    for metric in metrics:
        print(f"\nMetric: {metric}")
        for model in weighted_scores:
            print(f"{model}: {avg_metric_scores[model][metric]:.2f}")

    # 打印总分比较结果
    print("\nTotal Average Weighted Scores:")
    for model in total_scores:
        print(f"{model}: {total_scores[model]:.2f}")


# 比较各个模型在不同指标上的表现
compare_models_on_metrics(weighted_scores)
